package Entrily.src.Base;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.chrome.ChromeDriver;import org.openqa.selenium.firefox.FirefoxDriver;import org.openqa.selenium.support.ui.ExpectedConditions;import org.openqa.selenium.support.ui.Select;import org.openqa.selenium.support.ui.WebDriverWait;import java.io.File;import java.time.Duration;public class Base {    public static WebDriver driver;    public static WebDriver GetDriver(String a )    {        if(a.equals("Google"))        {           driver = new ChromeDriver();        }        else {            driver =  new FirefoxDriver();        }        return driver;    }    public static void GetURL()    {        driver.manage().window().maximize();        driver.get("https://demoentrilycrm.gaipp.com/crm/dashboard/summary");        driver.findElement(By.id("user")).sendKeys("info.entrily@gmail.com");        driver.findElement(By.id("password")).sendKeys("");        driver.findElement(By.id("submit")).click();        String d  =ToastMessage(By.id("toast-container"));        System.out.println(d);        driver.manage().timeouts().implicitlyWait(Duration.ofSeconds(5));        waitForURL("https://demoentrilycrm.gaipp.com/crm/dashboard/summary");        waitForTitle("Entrily");    }    public static void waitForURL(String url)    {        boolean a  = new WebDriverWait(driver,(Duration.ofSeconds(5))).until(ExpectedConditions.urlToBe(url));        System.out.println(a+"This URL waiting  is passed ");    }    public static void waitForTitle(String title)    {        boolean a = new WebDriverWait(driver,(Duration.ofSeconds(5))).until(ExpectedConditions.titleIs(title));        System.out.println(a+"This Title  waiting  is passed ");    }    public  static void  SelectByValue(WebElement a , String value )    {        Select oc = new Select(a);        oc.selectByValue(value);    }    public  static void  SelectByVisibility (WebElement a , String value )    {        Select oc = new Select(a);        oc.selectByVisibleText(value);    }    public static String ToastMessage(By ToastLocator )    {      WebElement b =  new WebDriverWait(driver,(Duration.ofSeconds(5))).until(ExpectedConditions.presenceOfElementLocated(ToastLocator));      String c= b.getText();      return  c;    }    public static  void FileUpload(String b , String FilePath) {        WebElement Fileload = driver.findElement(By.id(b));        File uploadFile = new File(FilePath);        Fileload.sendKeys(uploadFile.getAbsolutePath());    }}